import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import EnginesPage from './EnginesPage';
import {
  FaCogs, FaTachometerAlt, FaBolt, FaCarBattery, FaSnowflake, FaTools,
  FaCarSide, FaFan, FaGasPump, FaWrench, FaMusic, FaLightbulb,
  FaMapMarkerAlt, FaSyncAlt, FaSlidersH, FaExclamationTriangle,
  FaCar, FaWater, FaMicrochip, FaPlug, FaBicycle, FaSearch,
  FaToolbox, FaCog, FaBoxOpen
} from 'react-icons/fa';

const categories = [
  { name: 'Engines', icon: <FaCogs /> },
  { name: 'Brakes', icon: <FaTachometerAlt /> },
  { name: 'Suspension', icon: <FaSlidersH /> },
  { name: 'Transmission', icon: <FaSyncAlt /> },
  { name: 'Exhaust', icon: <FaExclamationTriangle /> },
  { name: 'Wheels & Tires', icon: <FaCarSide /> },
  { name: 'Electrical Systems', icon: <FaBolt /> },
  { name: 'Lighting', icon: <FaLightbulb /> },
  { name: 'Batteries', icon: <FaCarBattery /> },
  { name: 'Filters', icon: <FaFan /> },
  { name: 'Cooling Systems', icon: <FaSnowflake /> },
  { name: 'Steering', icon: <FaToolbox /> },
  { name: 'Fuel Systems', icon: <FaGasPump /> },
  { name: 'Ignition', icon: <FaWrench /> },
  { name: 'Sensors', icon: <FaMicrochip /> },
  { name: 'Tools & Equipment', icon: <FaTools /> },
  { name: 'Seals', icon: <FaBoxOpen /> },
  { name: 'Drive Belts', icon: <FaBicycle /> },
  { name: 'Air Conditioning', icon: <FaFan /> },
  { name: 'Expansion Bottle', icon: <FaWater /> },
  { name: 'Thermostats', icon: <FaMapMarkerAlt /> },
  { name: 'Gaskets', icon: <FaCog /> },
  { name: 'Bearings', icon: <FaPlug /> },
  { name: 'Clutch Kits', icon: <FaBolt /> },
  { name: 'Timing Components', icon: <FaSearch /> },
  { name: 'Maintenance Kits', icon: <FaTools /> }
];

export default function PartsCategoriesPage() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [engineSheets, setEngineSheets] = useState({});
  const [showTopBtn, setShowTopBtn] = useState(false);

  useEffect(() => {
    if (selectedCategory === 'Engines') {
      fetch('/engines.xlsx')
        .then(res => res.arrayBuffer())
        .then(buffer => {
          const workbook = XLSX.read(buffer, { type: 'array' });
          const sheets = {};
          workbook.SheetNames.forEach(name => {
            const sheet = workbook.Sheets[name];
            const data = XLSX.utils.sheet_to_json(sheet, { defval: '' });
            sheets[name] = data;
          });
          setEngineSheets(sheets);
        });
    }
  }, [selectedCategory]);

  useEffect(() => {
    const handleScroll = () => setShowTopBtn(window.scrollY > 300);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: 'smooth' });

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-blue-100 p-8">
      <h1 className="text-4xl font-extrabold text-center mb-14 text-gray-900 tracking-wide select-none drop-shadow-md">
        Browse Parts by Category
      </h1>

      {selectedCategory === 'Engines' ? (
  <div className="mb-6 max-w-6xl mx-auto">
    <button
      onClick={() => setSelectedCategory(null)}
      className="text-blue-700 hover:text-blue-900 transition font-semibold mb-8 flex items-center gap-3 text-lg"
    >
      ← Back to Categories
    </button>
          <h2 className="text-3xl font-semibold mb-5 text-gray-800">Engine Sheets</h2>
          {Object.entries(engineSheets).map(([sheetName, rows]) => (
            <div key={sheetName} className="mb-10 bg-white shadow rounded-2xl p-6">
              <h3 className="text-2xl font-bold mb-4 text-blue-700">{sheetName}</h3>
              <div className="overflow-x-auto">
                <table className="min-w-full text-sm border border-gray-200 rounded">
                  <thead>
                    <tr className="bg-gray-100">
                      {rows[0] && Object.keys(rows[0]).map((col, i) => (
                        <th key={i} className="border p-2 text-left">{col}</th>
                      ))}
                    </tr>
                  </thead>
                  <tbody>
                    {rows.map((row, idx) => (
                      <tr key={idx} className="hover:bg-gray-50">
                        {Object.values(row).map((cell, i) => (
                          <td key={i} className="border p-2">{cell}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="max-w-7xl mx-auto grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-6">
          {categories.map((cat, idx) => (
            <button
              key={idx}
              onClick={() => setSelectedCategory(cat.name)}
              className="bg-white border border-gray-300 rounded-2xl p-8 flex flex-col items-center justify-center
                text-center text-gray-900 shadow-md hover:shadow-2xl transition-all duration-300 transform hover:scale-105
                focus:outline-none focus:ring-4 focus:ring-blue-300"
            >
              <div className="text-5xl mb-3 text-blue-600">{cat.icon}</div>
              <div className="font-semibold text-lg">{cat.name}</div>
            </button>
          ))}
        </div>
      )}

      {showTopBtn && (
        <button
          onClick={scrollToTop}
          className="fixed bottom-8 right-8 bg-blue-600 hover:bg-blue-700 text-white py-3 px-5 rounded-full
          shadow-lg transition duration-300 ease-in-out focus:outline-none focus:ring-4 focus:ring-blue-500"
          aria-label="Back to top"
        >
          ↑ Back to Top
        </button>
      )}
    </div>
  );
}
