import React, { useEffect, useState } from 'react';
import * as XLSX from 'xlsx';

export default function EngineSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [allData, setAllData] = useState([]);

  useEffect(() => {
    const loadExcel = async (url, label) => {
      try {
        const res = await fetch(url);
        const arrayBuffer = await res.arrayBuffer();
        const workbook = XLSX.read(arrayBuffer, { type: 'array' });

        let combined = [];
        workbook.SheetNames.forEach(sheetName => {
          const sheet = workbook.Sheets[sheetName];
          const rows = XLSX.utils.sheet_to_json(sheet);

          rows.forEach(row => {
            combined.push({
              source: label,     // Indicate file source
              sheet: sheetName,  // Indicate sheet name
              ...row,
            });
          });
        });

        return combined;
      } catch (err) {
        console.error(`Error loading ${url}:`, err);
        return [];
      }
    };

    const loadAllData = async () => {
      const engines = await loadExcel('/engines.xlsx', 'engines');
      const sparkplugs = await loadExcel('/sparkplugs.xlsx', 'sparkplugs');

      setAllData([...engines, ...sparkplugs]);
    };

    loadAllData();
  }, []);

  useEffect(() => {
    const filtered = allData.filter(row => {
      const term = searchTerm.toLowerCase();
      return Object.values(row).some(val =>
        String(val).toLowerCase().includes(term)
      );
    });
    setResults(filtered);
  }, [searchTerm, allData]);

  return (
    <div className="max-w-4xl mx-auto py-10">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">
        Search Engine Parts
      </h2>
      <input
        type="text"
        placeholder="Search parts, model, make..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        className="w-full p-3 rounded-xl border border-gray-300 shadow-sm focus:ring-2 focus:ring-blue-400 mb-8"
      />

      {results.length > 0 ? (
        <ul className="space-y-4">
          {results.map((item, index) => (
            <li
              key={index}
              className="bg-white rounded-xl p-5 shadow hover:shadow-md transition border border-gray-100"
            >
              <div className="text-lg font-semibold text-blue-800">
                {item['part name'] || item['description'] || 'Unnamed Part'}
              </div>
              <div className="text-sm text-gray-600">
                {item.model && <span className="mr-3">Model: {item.model}</span>}
                {item.make && <span className="mr-3">Make: {item.make}</span>}
                {item.qty && <span>Qty: {item.qty}</span>}
              </div>
              <div className="text-xs text-gray-400 mt-1">
                From: {item.source} / {item.sheet}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-center text-gray-500 italic">No matching results</p>
      )}
    </div>
  );
}
