import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
// import PartsFrontPage from './components/PartsCategoriesPage';
import {
  FaCogs,
  FaTachometerAlt,
  FaBolt,
  FaCarBattery,
  FaSnowflake,
  FaTools,
  FaCarSide,
  FaFan,
  FaGasPump,
  FaWrench,
  FaMusic,
  FaLightbulb,
  FaMapMarkerAlt,
  FaSyncAlt,
  FaSlidersH,
  FaExclamationTriangle,
  FaCar,
  FaWater,
  FaMicrochip,
  FaBoltLightning,
  FaPlug,
  FaBicycle,
  FaSearch,
  FaToolbox,
  FaCog,
  FaTools as FaToolsAlt,
  FaBoxOpen
} from 'react-icons/fa';

// Sample data for filtering (can expand or load from API)
const sampleParts = [
  { name: 'V8 Engine', category: 'Engines' },
  { name: 'ABS Brake Kit', category: 'Brakes' },
  { name: 'Car Battery 12V', category: 'Batteries' },
  { name: 'LED Headlights', category: 'Lighting' },
  { name: 'Cabin Air Filter', category: 'Filters' },
  // Add more sample parts here
];

const categories = [
  { name: 'Engines', icon: <FaCogs /> },
  { name: 'Brakes', icon: <FaTachometerAlt /> },
  { name: 'Suspension', icon: <FaSlidersH /> },
  { name: 'Transmission', icon: <FaSyncAlt /> },
  { name: 'Exhaust', icon: <FaExclamationTriangle /> },
  { name: 'Wheels & Tires', icon: <FaExclamationTriangle /> },
  { name: 'Electrical Systems', icon: <FaBolt /> },
  { name: 'Lighting', icon: <FaLightbulb /> },
  { name: 'Interior Parts', icon: <FaCarSide /> },
  { name: 'Exterior Parts', icon: <FaCar /> },
  { name: 'Batteries', icon: <FaCarBattery /> },
  { name: 'Filters', icon: <FaFan /> },
  { name: 'Cooling Systems', icon: <FaSnowflake /> },
  { name: 'Steering', icon: <FaToolbox /> },
  { name: 'Fuel Systems', icon: <FaGasPump /> },
  { name: 'Ignition', icon: <FaWrench /> },
  { name: 'Sensors', icon: <FaMicrochip /> },
  { name: 'Tools & Equipment', icon: <FaTools /> },
  { name: 'Seals', icon: <FaBoxOpen /> },
  { name: 'Drive Belts', icon: <FaBicycle /> },
  { name: 'Air Conditioning', icon: <FaWater /> },
  { name: 'Audio Systems', icon: <FaMusic /> },
  { name: 'Thermostats', icon: <FaMapMarkerAlt /> },
  { name: 'Gaskets', icon: <FaCog /> },
  { name: 'Bearings', icon: <FaPlug /> },
  { name: 'Clutch Kits', icon: <FaBolt/> },
  { name: 'Timing Components', icon: <FaSearch /> },
  { name: 'Maintenance Kits', icon: <FaToolsAlt /> },
];

export default function PartsCategoriesPage() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showTopBtn, setShowTopBtn] = useState(false);

  useEffect(() => {
    const handleScroll = () => setShowTopBtn(window.scrollY > 300);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: 'smooth' });

  // Filter parts by selected category (placeholder logic)
  const filteredParts = selectedCategory
    ? sampleParts.filter((part) => part.category === selectedCategory)
    : [];

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-100 to-blue-50 p-6">
      <h1 className="text-4xl font-bold text-center mb-10 text-gray-800">
        Browse Parts by Category
      </h1>

      {selectedCategory ? (
        <div className="mb-6">
          <button
            onClick={() => setSelectedCategory(null)}
            className="text-blue-600 hover:underline mb-4"
          >
            ← Back to Categories
          </button>
          <h2 className="text-2xl font-semibold mb-3">{selectedCategory} Parts</h2>
          {filteredParts.length > 0 ? (
            <ul className="list-disc list-inside text-gray-700">
              {filteredParts.map((part, idx) => (
                <li key={idx}>{part.name}</li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500">No parts found for this category.</p>
          )}
        </div>
      ) : (
        <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-6">
          {categories.map((cat, idx) => (
            <button
              key={idx}
              onClick={() => setSelectedCategory(cat.name)}
              className="bg-white hover:bg-blue-50 transition p-6 rounded-2xl flex flex-col items-center text-center border border-gray-200 shadow-md"
            >
              <div className="text-4xl text-blue-600 mb-2">{cat.icon}</div>
              <div className="font-semibold text-lg text-gray-800">{cat.name}</div>
            </button>
          ))}
        </div>
      )}

      {showTopBtn && (
        <button
          onClick={scrollToTop}
          className="fixed bottom-6 right-6 bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-full shadow-lg transition duration-300"
          aria-label="Back to top"
        >
          ↑ Back to Top
        </button>
      )}
    </div>
  );
}
