import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import {
  FaCogs, FaTachometerAlt, FaBolt, FaCarBattery, FaSnowflake, FaTools,
  FaCarSide, FaFan, FaGasPump, FaWrench, FaMusic, FaLightbulb,
  FaMapMarkerAlt, FaSyncAlt, FaSlidersH, FaExclamationTriangle,
  FaCar, FaWater, FaMicrochip, FaPlug, FaBicycle, FaSearch,
  FaToolbox, FaCog, FaBoxOpen
} from 'react-icons/fa';

const categories = [
  { name: 'Engines', icon: <FaCogs /> },
  { name: 'Braking', icon: <FaTachometerAlt /> },
  { name: 'Suspension', icon: <FaSlidersH /> },
  { name: 'Transmission', icon: <FaSyncAlt /> },
  { name: 'Exhaust', icon: <FaExclamationTriangle /> },
  { name: 'Wheels & Tires', icon: <FaCarSide /> },
  { name: 'Electrical Systems', icon: <FaBolt /> },
  { name: 'Lighting', icon: <FaLightbulb /> },
  { name: 'Batteries', icon: <FaCarBattery /> },
  { name: 'Filters', icon: <FaFan /> },
  { name: 'Cooling Systems', icon: <FaSnowflake /> },
  { name: 'Steering', icon: <FaToolbox /> },
  { name: 'Fuel Systems', icon: <FaGasPump /> },
  { name: 'Ignition', icon: <FaWrench /> },
  { name: 'Sensors', icon: <FaMicrochip /> },
  { name: 'Tools & Equipment', icon: <FaTools /> },
  { name: 'Seals', icon: <FaBoxOpen /> },
  { name: 'Drive Belts', icon: <FaBicycle /> },
  { name: 'Air Conditioning', icon: <FaFan /> },
  { name: 'Expansion Bottle', icon: <FaWater /> },
  { name: 'Thermostats', icon: <FaMapMarkerAlt /> },
  { name: 'Gaskets', icon: <FaCog /> },
  { name: 'Bearings', icon: <FaPlug /> },
  { name: 'Clutch Kits', icon: <FaBolt /> },
  { name: 'Timing Components', icon: <FaSearch /> },
  { name: 'Maintenance Kits', icon: <FaTools /> }
];

export default function PartsCategoriesPage() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [allData, setAllData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [groupedResults, setGroupedResults] = useState({});

  const filesToLoad = {
    Engines: [
      { url: '/engines.xlsx', label: 'engines' },
      { url: '/sparkplugs.xlsx', label: 'sparkplugs' }
    ]
  };

  useEffect(() => {
    const loadExcel = async (url, label) => {
      try {
        const res = await fetch(url);
        const buffer = await res.arrayBuffer();
        const workbook = XLSX.read(buffer, { type: 'array' });
        let data = [];
        workbook.SheetNames.forEach(name => {
          const sheet = XLSX.utils.sheet_to_json(workbook.Sheets[name], { defval: '' });
          data = data.concat(sheet.map(row => ({ ...row, sheet: name, source: label })));
        });
        return data;
      } catch (error) {
        console.error('Failed to load:', url, error);
        return [];
      }
    };

    const loadCategoryData = async () => {
      if (!selectedCategory || !filesToLoad[selectedCategory]) return;
      let combined = [];
      for (const file of filesToLoad[selectedCategory]) {
        const data = await loadExcel(file.url, file.label);
        combined = combined.concat(data);
      }
      setAllData(combined);
    };

    if (selectedCategory) loadCategoryData();
    else setAllData([]);
  }, [selectedCategory]);

  useEffect(() => {
    const filtered = allData.filter(row =>
      Object.values(row).some(val =>
        String(val).toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
    const grouped = filtered.reduce((acc, item) => {
      const group = item.sheet || 'Other';
      acc[group] = acc[group] || [];
      acc[group].push(item);
      return acc;
    }, {});
    setGroupedResults(grouped);
  }, [searchTerm, allData]);

  const renderCard = (item, index) => {
    const label = Object.entries(item)
      .find(([k, v]) => v && !['sheet', 'source', 'Qty', 'QTY', 'NOTE'].includes(k));
    return (
      <div
        key={index}
        className="bg-white rounded-2xl shadow hover:shadow-lg p-4 border border-gray-200"
      >
        <h4 className="font-semibold text-blue-800 mb-2 text-md truncate">
          {label ? label[1] : 'Unnamed Part'}
        </h4>
        <ul className="text-sm text-gray-700 space-y-1">
          {Object.entries(item).map(([k, v]) => (
            k !== 'sheet' && k !== 'source' && v && (
              <li key={k}><strong>{k}:</strong> {v}</li>
            )
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-100 py-10 px-6">
      <h1 className="text-4xl font-extrabold text-center mb-10 text-gray-900">Browse Parts</h1>

      {selectedCategory ? (
        <div className="max-w-6xl mx-auto">
          <button
            onClick={() => setSelectedCategory(null)}
            className="text-blue-600 hover:text-blue-800 font-semibold mb-6 flex items-center gap-2"
          >
            ← Back to Categories
          </button>

          <input
            type="text"
            placeholder="Search parts, model, make..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            className="w-full mb-8 p-3 rounded-lg border border-gray-300 shadow focus:outline-none focus:ring-2 focus:ring-blue-400"
          />

          {Object.keys(groupedResults).length ? (
            Object.entries(groupedResults).map(([sheet, items]) => (
              <div key={sheet} className="mb-10">
                <h2 className="text-xl font-bold text-blue-700 mb-4 border-b pb-1">{sheet}</h2>
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
                  {items.map(renderCard)}
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-500 italic">No matching parts found.</p>
          )}
        </div>
      ) : (
        <div className="max-w-7xl mx-auto grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-6">
          {categories.map((cat, i) => (
            <button
              key={i}
              onClick={() => setSelectedCategory(cat.name)}
              className="bg-white border border-gray-200 rounded-2xl p-6 flex flex-col items-center justify-center text-center shadow hover:shadow-lg transition hover:scale-105"
            >
              <div className="text-4xl text-blue-500 mb-2">{cat.icon}</div>
              <span className="font-semibold text-sm text-gray-800">{cat.name}</span>
            </button>
          ))}
        </div>
      )}
    </div>
  );
}
