import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import EnginesPage from './EnginesPage';
import {
  FaCogs, FaTachometerAlt, FaBolt, FaCarBattery, FaSnowflake, FaTools,
  FaCarSide, FaFan, FaGasPump, FaWrench, FaMusic, FaLightbulb,
  FaMapMarkerAlt, FaSyncAlt, FaSlidersH, FaExclamationTriangle,
  FaCar, FaWater, FaMicrochip, FaPlug, FaBicycle, FaSearch,
  FaToolbox, FaCog, FaBoxOpen
} from 'react-icons/fa';

const categories = [
  { name: 'Engines', icon: <FaCogs /> },
  { name: 'Brakes', icon: <FaTachometerAlt /> },
  { name: 'Suspension', icon: <FaSlidersH /> },
  { name: 'Transmission', icon: <FaSyncAlt /> },
  { name: 'Exhaust', icon: <FaExclamationTriangle /> },
  { name: 'Wheels & Tires', icon: <FaCarSide /> },
  { name: 'Electrical Systems', icon: <FaBolt /> },
  { name: 'Lighting', icon: <FaLightbulb /> },
  { name: 'Batteries', icon: <FaCarBattery /> },
  { name: 'Filters', icon: <FaFan /> },
  { name: 'Cooling Systems', icon: <FaSnowflake /> },
  { name: 'Steering', icon: <FaToolbox /> },
  { name: 'Fuel Systems', icon: <FaGasPump /> },
  { name: 'Ignition', icon: <FaWrench /> },
  { name: 'Sensors', icon: <FaMicrochip /> },
  { name: 'Tools & Equipment', icon: <FaTools /> },
  { name: 'Seals', icon: <FaBoxOpen /> },
  { name: 'Drive Belts', icon: <FaBicycle /> },
  { name: 'Air Conditioning', icon: <FaFan /> },
  { name: 'Expansion Bottle', icon: <FaWater /> },
  { name: 'Thermostats', icon: <FaMapMarkerAlt /> },
  { name: 'Gaskets', icon: <FaCog /> },
  { name: 'Bearings', icon: <FaPlug /> },
  { name: 'Clutch Kits', icon: <FaBolt /> },
  { name: 'Timing Components', icon: <FaSearch /> },
  { name: 'Maintenance Kits', icon: <FaTools /> }
];

export default function EngineSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [groupedResults, setGroupedResults] = useState({});
  const [allData, setAllData] = useState([]);

  useEffect(() => {
    const loadExcel = async () => {
      try {
        const res = await fetch('/engines.xlsx');
        const arrayBuffer = await res.arrayBuffer();
        const workbook = XLSX.read(arrayBuffer, { type: 'array' });

        let combined = [];
        workbook.SheetNames.forEach(sheetName => {
          const sheet = workbook.Sheets[sheetName];
          const rows = XLSX.utils.sheet_to_json(sheet);

          rows.forEach(row => {
            combined.push({
              sheet: sheetName,
              ...row,
            });
          });
        });

        setAllData(combined);
      } catch (err) {
        console.error('Error loading Excel:', err);
      }
    };

    loadExcel();
  }, []);

  useEffect(() => {
    const term = searchTerm.toLowerCase();
    const filtered = allData.filter(row =>
      Object.values(row).some(val =>
        String(val).toLowerCase().includes(term)
      )
    );

    // Group by sheet
    const grouped = filtered.reduce((acc, item) => {
      const sheet = item.sheet || 'Other';
      if (!acc[sheet]) acc[sheet] = [];
      acc[sheet].push(item);
      return acc;
    }, {});

    setGroupedResults(grouped);
  }, [searchTerm, allData]);

  return (
    <div className="max-w-5xl mx-auto py-10">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">
        Search Engine Parts
      </h2>
      <input
        type="text"
        placeholder="Search parts, model, make..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        className="w-full p-3 rounded-xl border border-gray-300 shadow-sm focus:ring-2 focus:ring-blue-400 mb-8"
      />

      {Object.keys(groupedResults).length > 0 ? (
        <div className="space-y-10">
          {Object.entries(groupedResults).map(([sheetName, items]) => (
            <div key={sheetName}>
              <h3 className="text-2xl font-semibold text-blue-700 mb-4 border-b pb-1">{sheetName}</h3>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                {items.map((item, index) => (
                  <div
                    key={index}
                    className="bg-white rounded-xl p-5 shadow hover:shadow-md transition border border-gray-100"
                  >
                    {(() => {
                      const exclude = ['sheet', 'Qty', 'QTY', 'NOTE'];
                      const firstField = Object.entries(item).find(
                        ([key, val]) => key && val && !exclude.includes(key)
                      );
                      return (
                        <h4 className="text-lg font-semibold text-blue-800 mb-1">
                          {firstField ? `${firstField[1]}` : ''}
                        </h4>
                      );
                    })()}
                    <ul className="text-sm text-gray-700 space-y-1">
                      {Object.entries(item).map(([key, value]) => (
                        key !== 'sheet' && value !== '' && (
                          <li key={key}><strong>{key}:</strong> {value}</li>
                        )
                      ))}
                    </ul>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-500 italic">No matching results</p>
      )}
    </div>
  );
}
